from langflow.custom import Component
from langflow.io import StrInput, Output
from langflow.schema import Data
import base64


class BinaryStringToBase64(Component):
    display_name = "Binary String to Base64"
    description = "Converts a Python-style binary string (e.g., b'...') into base64."
    icon = "mdi-encode"
    name = "BinaryStringToBase64"
    field_order = ["binary_string"]

    inputs = [
        MultilineInput(
            name="binary_string",
            display_name="Binary String",
            info="String containing a binary literal (e.g., b'...')",
            required=True,
        ),
    ]

    outputs = [
        Output(name="base64_output", display_name="Base64 Output", method="convert_to_base64"),
    ]

    def build(self):
        return self.convert_to_base64()

    def convert_to_base64(self) -> Data:
        try:
            # Attempt to safely evaluate the binary string literal
            # Remove the leading b' and trailing ', then decode escape sequences
            if self.binary_string.startswith("b'") and self.binary_string.endswith("'"):
                raw = self.binary_string[2:-1].encode('utf-8').decode('unicode_escape').encode('latin1')
                b64_encoded = base64.b64encode(raw).decode('utf-8')
                return Data(text=b64_encoded)
            else:
                error_msg = "Input must be a binary string literal (e.g., b'...')"
                self.status = error_msg
                return Data(data={"error": error_msg})

        except Exception as e:
            error_msg = f"Error converting to base64: {str(e)}"
            self.status = error_msg
            return Data(data={"error": error_msg})
